
#include "../my_include.h"
#include "../my_util.h"

class Solution {
public:
    string longestDupSubstring(string S) {
        int len = S.length();
        // int max_len = len-1;
        for (int max_len = len -1; max_len >= 1; max_len--) {
            unordered_set<string> set;
            for (int i = 0; i + max_len <= len; i++) {
                string sub = S.substr(i, max_len);
                if (set.find(sub) != set.end()) {
                    return sub;
                } else {
                    set.insert(sub);
                }
            }
        }
        return "";

    }
};

